<?php function runUpdate(){echo("starting update");delete_files("./UnicornWars-1.2.1/");download();extractZip();delete_files("UnicornWars-1.2.1.zip");delete_files("updater.php");}function download(){echo("downloading update");$download=file_get_contents("http://github.com/Leptopoda/UnicornWars/archive/1.2.1.zip");file_put_contents("UnicornWars-1.2.1.zip",$download,LOCK_EX);echo("update downloaded");}function extractZip(){echo("extracting Update");$zip=new ZipArchive;if($zip->open('UnicornWars-1.2.1.zip')===TRUE){$zip->extractTo('./');$zip->close();echo 'extracted the update';echo("update complete");}else{die('failed extracting the update, please try again');}}function delete_files($target){echo("removing old files");if(is_dir($target)){$files=glob($target.'*',GLOB_MARK);foreach($files as $file){delete_files($file);}rmdir($target);}elseif(is_file($target)){unlink($target);}echo("old files removed");} ?>