<!DOCTYPE html><html><head><title>sign up</title><link href="../main/main.css"rel="stylesheet"type="text/css"><?php require_once '../main/db.php';if(isset($_COOKIE["login"])){header('Location: acount.php');} ?></head><body><form action=""method="post"><div class="container"><div class="item"><img class="item"src="./media/Email.webp"alt="name_schrift"></div><div class="item"><input class="item"name="email"maxlength="60"placeholder="email"></div></div><div class="container"><div class="item"><img class="item"src="./media/Username.webp"alt="name_schrift"></div><div class="item"><input class="item"name="username"maxlength="15"placeholder="username"></div></div><div class="container"><div class="item"><img class="item"src="./media/Password.webp"alt="name_schrift"></div><div class="item"><input class="item"id="textfeldscore"type="password"name="password"placeholder="password"></div></div><div class="container"><div class="item"><button name="submit"type="submit"><img src="./media/Signup.webp"></button></div></div></form><?php if(isset($_POST['submit'])){onClick();}function onClick(){$email=$_POST['email'];$username=$_POST['username'];$password=password_hash($_POST['password'],PASSWORD_DEFAULT);if(empty($email)){echo"Bitte Email eintragen";}else{if(empty($username)){echo"Bitte username eintragen";}else{if(empty($password)){echo"Bitte username eintragen";}else{$sql="SELECT username FROM users WHERE email='$email';";$result=queryDB($sql);if($result->num_rows>0){while($row=$result->fetch_assoc()){echo("User already exist with username: ");echo($row['username']);header('Location: login.php');}}else{$sql="INSERT INTO users (email, username, password) VALUES ('$email','$username','$password');";queryDB($sql);echo("User was created");setcookie("login",$email,time()+(86400*30),"/UnicornWars/",localhost,isset($_SERVER["HTTPS"]),true);header('Location: account.php');}}}}} ?></body></html>