<!DOCTYPE html><html><head><title>Home</title><link href="../Styles/main.css"rel="stylesheet"type="text/css"><link href="../Styles/account.css"rel="stylesheet"type="text/css"><?php include_once 'db.php';if(!isset($_COOKIE["login"])){echo "login denied";header('Location: ../site/login.php');}else{$email=$_COOKIE["login"];} ?></head><body><div class="container"><form action=""method="post"><button name="logout"type="submit"class="item"id="logout"><img src="../Images/button.webp"></button></form></div><?php if(isset($_POST['logout'])){logout();}function logout(){setcookie("login","",time()-3600,"/UnicornWars/");echo("you've been loged out");header('Location: ../site/login.php');}$sql="SELECT lastlogin, username FROM users WHERE email='$email';";$result=queryDB($sql);if($result->num_rows>0){while($row=$result->fetch_assoc()){echo 'your email is: ',$email;echo 'your username is: ',$row['username'];echo 'your last login was: ',$row['lastlogin'];}} ?></body></html>