<!DOCTYPE html><html><head><title>Game Over</title><link href="../Styles/main.css"rel="stylesheet"type="text/css"></head><body><?php include_once 'db.php';@$score=$_POST['dauer'];if(empty($score)){$visible="none";$score="Bitte erst spielen";}else{$visible="visible";} ?><form action=""method="post"><div class="container"><div class="item"><img src="../Images/name.webp"alt="name_schrift"class="item"></div><div class="item"><input class="item"name="Username"placeholder="Bitte Name Eintragen"maxlength="15"id="textfeldname"></div></div><div class="container"><div class="item"><img src="../Images/score.webp"alt="score_schrift"class="item"></div><div class="item"><input class="item"name="score"id="textfeldscore"readonly value="<?php echo $score; ?>"></div></div><div class="container"><div class="item"><button name="submit"type="submit"id="buttoneintragen"style="display:<?php echo$visible ?>"><img src="../Images/abschicken.webp"></button></div><div class="item"><a href="../site/scoreboard.php"><img src="../Images/scoreboard.webp"alt="continue_schrift"></a></div></div></form><?php if(isset($_POST['submit'])){onClick();}function onClick(){$Username=$_POST['Username'];$score=$_POST['score'];if(empty($Username)){echo"Bitte Namen eintragen";}else{$sql="INSERT INTO scoreboard (name,score) VALUES ('$Username', '$score')";queryDB($sql);header('Location: ../site/scoreboard.php');}} ?></body></html>