<!DOCTYPE html><html><head><title>login</title><link href="../Styles/main.css"rel="stylesheet"type="text/css"><?php include_once 'db.php';if(isset($_COOKIE["login"])){header('Location: ../site/Acount.php');} ?></head><body><form action=""method="post"><div class="container"><div class="item"><img src="../Images/Email.webp"alt="name_schrift"class="item"></div><div class="item"><input class="item"name="email"placeholder="email"maxlength="60"></div></div><div class="container"><div class="item"><img src="../Images/Password.webp"alt="name_schrift"class="item"></div><div class="item"><input class="item"name="password"placeholder="password"id="textfeldscore"type="password"></div></div><div class="container"><div class="item"><button name="submit"type="submit"><img src="../Images/Login.webp"></button></div></div></form><?php if(isset($_POST['submit'])){onClick();}function onClick(){$email=$_POST['email'];$time=date('Y-m-d H:i:s');if(empty($email)){echo"Bitte Email eintragen";}else{if(empty($_POST['password'])){echo"Bitte passwort eintragen";}else{$sql="SELECT password FROM users WHERE email='$email';";$result=queryDB($sql);if($result->num_rows>0){while($row=$result->fetch_assoc()){if(password_verify($_POST['password'],$row['password'])){echo 'Password is valid!';$sql="UPDATE users SET lastlogin='$time' WHERE email='$email';";queryDB($sql);setcookie("login",$email,time()+(86400*30),"/UnicornWars/");header('Location: ../site/Account.php');}else{echo 'Invalid password.';}}}}}} ?></body></html>