<!DOCTYPE html><html><head><title>sign up</title><link href="../Styles/main.css"rel="stylesheet"type="text/css"><?php include_once 'db.php';if(isset($_COOKIE["login"])){header('Location: ../site/Account.php');} ?></head><body><form action=""method="post"><div class="container"><div class="item"><img src="../Images/Email.webp"alt="name_schrift"class="item"></div><div class="item"><input class="item"name="email"placeholder="email"maxlength="60"></div></div><div class="container"><div class="item"><img src="../Images/Username.webp"alt="name_schrift"class="item"></div><div class="item"><input class="item"name="username"placeholder="username"maxlength="15"></div></div><div class="container"><div class="item"><img src="../Images/Password.webp"alt="name_schrift"class="item"></div><div class="item"><input class="item"name="password"placeholder="password"id="textfeldscore"type="password"></div></div><div class="container"><div class="item"><button name="submit"type="submit"><img src="../Images/Signup.webp"></button></div></div></form><?php if(isset($_POST['submit'])){onClick();}function onClick(){$email=$_POST['email'];$username=$_POST['username'];$password=password_hash($_POST['password'],PASSWORD_DEFAULT);if(empty($email)){echo"Bitte Email eintragen";}else{if(empty($username)){echo"Bitte username eintragen";}else{if(empty($password)){echo"Bitte username eintragen";}else{$sql="SELECT username FROM users WHERE email='$email';";$result=queryDB($sql);if($result->num_rows>0){while($row=$result->fetch_assoc()){echo("User already exist with username: ");echo($row['username']);header('Location: ../site/login.php');}}else{$sql="INSERT INTO users (email, username, password) VALUES ('$email','$username','$password');";queryDB($sql);echo("User was created");header('Location: ../site/Account.php');}}}}} ?></body></html>